SELECT ag.paeds_adult_age_group as "Patient category", "First Name", "Last Name","ART Number Legacy", "Date Of Birth","Contact Number" as "Phone 1", "Alt Contact Number" as "Phone 2","Current Age","Sex", "Last PBFW" as "Pregnancy/BF status", "Last Visit Date", "Last Next Visit Date" as "Next Follow-up Date", "Last Viral Load Order Date" as "Last VL order date", "Last Viral Load Result Date" as "Last VL result date",  "TPT Start Date", "TPT Type" as "TPT Regimen", "TPT Actual Stop Date","TPT Expected Stop Date", "TBT Regimen" as "TB Regimen", "TBT Start Date" as "TB Start Date", "TBT Expected Stop Date" as "TB Expected Stop Date", "TBT Outcome" as "Last TB Outcome","Last Regimen" as "Current ART Regimen", "Last Regimen Date" as "Current ART Regimen start date", "TPT Status Two", "Client Status" FROM analysis.fact_sentinel_event se INNER JOIN analysis.dim_age_group ag ON se."Current Age"=ag.age WHERE  "TX Curr" = 'Yes'