SELECT categories."MMP Status" AS category, COUNT(a."MMP Status") AS value, CASE WHEN (SELECT COUNT(*) FROM analysis.fact_sentinel_event WHERE "Current Age" > 19 AND "MMP Status Current" IS NOT NULL AND "TX Curr" = 'Yes') = 0 THEN '0%' ELSE CONCAT(ROUND((COUNT(a."MMP Status") * 100.0 / (SELECT COUNT(*) FROM analysis.fact_sentinel_event WHERE "Current Age" > 19 AND "MMP Status Current" IS NOT NULL AND "TX Curr" = 'Yes')), 1), '%') END AS percentage FROM (SELECT '< 3 MMP' AS "MMP Status" UNION ALL SELECT '3-5 MMP' UNION ALL SELECT '6+ MMP') AS categories LEFT JOIN (SELECT "MMP Status Current" AS "MMP Status", "Sex" AS "Sex", "Tri Pillar Age Group" AS age_group FROM analysis.fact_sentinel_event WHERE "TX Curr" = 'Yes' AND "Current Age" > 19 AND "MMP Status Current" IS NOT NULL ) a ON categories."MMP Status" = a."MMP Status" GROUP BY categories."MMP Status" ORDER BY CASE WHEN categories."MMP Status" = '< 3 MMP' THEN 3 WHEN categories."MMP Status" = '3-5 MMP' THEN 2 WHEN categories."MMP Status" = '6+ MMP' THEN 1 END